  selector: 'app-cursos',
  templateUrl: './cursos.component.html',
  styleUrls: ['./cursos.component.scss']
})
export class CursosComponent {

  nomePortal!: string;
  cursos: string[] = ['Java', 'Js', 'Angular']

  constructor(){
    this.nomePortal = "Thays"
  }

}
